mapper.select:
select shop_name,shop_latitude,shop_longitude,shop_id,shop_unique
from
shops
where 1=1
 and shop_unique=#{shop_unique}
 and manager_account=#{manager_account}
 and manager_unique=#{manager_unique}
 and shop_name=#{shop_name}
 and examinestatus=#{examinestatus}

select
shop_name,shop_phone,shop_unique from shops where
manager_account=#{manager_account} and manager_pwd=#{manager_pwd}

mapper.update:
update shops set shop_unique=#{shop_unique}
,shop_unique=#{shop_unique}
,shop_phone=#{shop_phone}
,shop_address_detail=#{shop_address_detail}
,manager_pwd=#{manager_pwd}
, shop_image_path=#{shop_image_path}
,shop_remark=#{shop_remark}
,power_supplier=#{power_supplier}
,examinestatus=#{examinestatus}
,examinestatus_reason=#{examinestatus_reason}
where shop_unique=#{shop_unique}

mapper.select:
select
manager_name,manager_phone,shop_name,shop_phone,shop_address_detail,shop_longitude,shop_latitude,shop_remark,examinestatus,shop_unique
from (
	(
	select
	manager_name,manager_phone,shop_name,shop_phone,shop_address_detail,shop_longitude,shop_latitude,shop_remark,examinestatus,shop_unique
	from shops s left join manager m on s.manager_unique=m.manager_unique left join area_dict a on a.area_dict_num=s.area_dict_num
	left join area_dict r on a.area_dict_parent_num=r.area_dict_num
	where 1=1
	 and shop_unique=#{shop_unique}
	 and shop_name like #{message}
	 and a.area_dict_num=#{area_dict_num}
	 and r.area_dict_num=#{carea_dict_num}
	 and r.area_dict_parent_num=#{parea_dict_num}
	 and examinestatus=#{examinestatus}
	)
	union
	(
	select
	manager_name,manager_phone,shop_name,shop_phone,shop_address_detail,shop_longitude,shop_latitude,shop_remark,examinestatus,shop_unique
	from shops s left join manager m on s.manager_unique=m.manager_unique left join area_dict a on a.area_dict_num=s.area_dict_num
	left join area_dict r on a.area_dict_parent_num=r.area_dict_num
	where 1=1
	 and shop_unique=#{shop_unique}
	 and manager_name like #{message}
	 and a.area_dict_num=#{area_dict_num}
	 and r.area_dict_num=#{carea_dict_num}
	 and r.area_dict_parent_num=#{parea_dict_num}
	 and examinestatus=#{examinestatus}
	)
	union
	(
	select
	manager_name,manager_phone,shop_name,shop_phone,shop_address_detail,shop_longitude,shop_latitude,shop_remark,examinestatus,shop_unique
	from shops s left join manager m on s.manager_unique=m.manager_unique left join area_dict a on a.area_dict_num=s.area_dict_num
	left join area_dict r on a.area_dict_parent_num=r.area_dict_num
	where 1=1
	 and shop_unique=#{shop_unique}
	 and shop_phone like #{message}
	 and a.area_dict_num=#{area_dict_num}
	 and r.area_dict_num=#{carea_dict_num}
	 and r.area_dict_parent_num=#{parea_dict_num}
	 and examinestatus=#{examinestatus}
	)
	union
	(
	select
	manager_name,manager_phone,shop_name,shop_phone,shop_address_detail,shop_longitude,shop_latitude,shop_remark,examinestatus,shop_unique
	from shops s left join manager m on s.manager_unique=m.manager_unique left join area_dict a on a.area_dict_num=s.area_dict_num
	left join area_dict r on a.area_dict_parent_num=r.area_dict_num
	where 1=1
	 and shop_unique=#{shop_unique}
	 and manager_phone like #{message}
	 and a.area_dict_num=#{area_dict_num}
	 and r.area_dict_num=#{carea_dict_num}
	 and r.area_dict_parent_num=#{parea_dict_num}
	 and examinestatus=#{examinestatus}
	)
) o

	limit #{startNum},#{pageSize}
